            //string hh = "<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.0 Transitional//EN\"><HTML><HEAD><TITLE>盘锦校区主页</TITLE>\r\n\r\n\r\n\r\n<!-- saved from url=(0026)http://panjin.dlut.edu.cn/ -->\r\n<STYLE type=\"text/css\">\r\n.box-mask {\r\n    position: fixed;\r\n    top: 0;\r\n    left: 0;\r\n    width: 100%;\r\n    height: 100%;\r\n    background: #000;\r\n    opacity: 0.5;\r\n    filter: alpha(opacity=50);\r\n    z-index: 99;\r\n    display: none;}\r\n\r\n.box {\r\n    position: absolute;\r\n    width: 1000px;\r\n    height: 620px;\r\n    \r\n    line-height:620px;\r\n    \r\n    text-align: center;\r\n    z-index: 101;\r\n    display: none;}\r\n.demo{width:100%; height:100%;margin:0 auto;}\r\n.btnDel {\r\nposition:absolute;\r\ntop:175px;\r\n    font: \"微软雅黑\";\r\n    font-size: 14px;\r\n    width: 125px;\r\n    height: 33px;\r\n    line-height: 33px;\r\n    vertical-align: middle;\r\n    background-image: url(images/btnits.png);\r\n    background-repeat: no-repeat;\r\n    border: 0px !important;\r\n    color: #fff;\r\n    cursor: pointer;\r\n    background-color: transparent;}\r\n</STYLE>\r\n<script src=\"js/jquery2015.min.js\"></script><script type=\"text/javascript\">\r\n$(document).ready(function() {\r\n    var isOpen = 0;\r\n    //全局变量，判断是否已经打开弹出框\r\n    $(\".btnDel\").click(function() {\r\n        //$(\".box-mask\").css({\"display\":\"block\"});\r\n        $(\".box-mask\").fadeIn(500);\r\n        center($(\".box\"));\r\n        //载入弹出窗口上的按钮事件\r\n        checkEvent($(this).parent(), $(\".btnSure\"), $(\".btnCancel\"));\r\n    });\r\n    function center(obj) {\r\n        //obj这个参数是弹出框的整个对象\r\n        var screenWidth = $(window).width(), screenHeigth = $(window).height();\r\n        //获取屏幕宽高\r\n        var scollTop = $(document).scrollTop();\r\n        //当前窗口距离页面顶部的距离\r\n        var objLeft = (screenWidth - obj.width()) / 2;\r\n        ///弹出框距离左侧距离\r\n        var objTop = (screenHeigth - obj.height()) / 2 + scollTop;\r\n        ///弹出框距离顶部的距离\r\n        obj.css({\r\n            left:objLeft + \"px\",\r\n            top:objTop + \"px\"\r\n        });\r\n        obj.fadeIn(500);\r\n        //弹出框淡入\r\n        isOpen = 1;\r\n        //弹出框打开后这个变量置1 说明弹出框是打开装填\r\n        //当窗口大小发生改变时\r\n        $(window).resize(function() {\r\n            //只有isOpen状态下才执行\r\n            if (isOpen == 1) {\r\n                //重新获取数据\r\n                screenWidth = $(window).width();\r\n                screenHeigth = $(window).height();\r\n                var scollTop = $(document).scrollTop();\r\n                objLeft = (screenWidth - obj.width()) / 2;\r\n                var objTop = (screenHeigth - obj.height()) / 2 + scollTop;\r\n                obj.css({\r\n                    left:objLeft + \"px\",\r\n                    top:objTop + \"px\"\r\n                });\r\n                obj.fadeIn(500);\r\n            }\r\n        });\r\n        //当滚动条发生改变的时候\r\n        $(window).scroll(function() {\r\n            if (isOpen == 1) {\r\n                //重新获取数据\r\n                screenWidth = $(window).width();\r\n                screenHeigth = $(window).height();\r\n                var scollTop = $(document).scrollTop();\r\n                objLeft = (screenWidth - obj.width()) / 2;\r\n                var objTop = (screenHeigth - obj.height()) / 2 + scollTop;\r\n                obj.css({\r\n                    left:objLeft + \"px\",\r\n                    top:objTop + \"px\"\r\n                });\r\n                obj.fadeIn(500);\r\n            }\r\n        });\r\n    }\r\n    //导入两个按钮事件 obj整个页面的内容对象，obj1为确认按钮，obj2为取消按钮\r\n    function checkEvent(obj, obj1, obj2) {\r\n        //确认后删除页面所有东西\r\n        obj1.click(function() {\r\n            //移除所有父标签内容\r\n            obj.remove();\r\n            //调用closed关闭弹出框\r\n            closed($(\".box-mask\"), $(\".box\"));\r\n        });\r\n        //取消按钮事件\r\n        obj2.click(function() {\r\n            //调用closed关闭弹出框\r\n            closed($(\".box-mask\"), $(\".box\"));\r\n        });\r\n    }\r\n    //关闭弹出窗口事件\r\n    function closed(obj1, obj2) {\r\n        obj1.fadeOut(500);\r\n        obj2.fadeOut(500);\r\n        isOpen = 0;\r\n    }\r\n});\r\n</script>\r\n<META content=\"text/html; charset=UTF-8\" http-equiv=\"Content-Type\"><LINK rel=\"stylesheet\" type=\"text/css\" href=\"style/main.css\"><LINK rel=\"stylesheet\" type=\"text/css\" href=\"style/menu.css\"><LINK rel=\"stylesheet\" type=\"text/css\" href=\"style/nivo-slider.css\"><LINK rel=\"stylesheet\" type=\"text/css\" href=\"style/style.css\"><script type=\"text/javascript\" src=\"js/jquery-1.4.3.min.js\"></script><script type=\"text/javascript\" src=\"js/jquery.nivo.slider.pack.js\"></script><script type=\"text/javascript\">\r\n    $(window).ready(function() {\r\n    //$(window).load(function() {\r\n        $('#slider').nivoSlider();\r\n    });\r\n</script>\r\n<META name=\"GENERATOR\" content=\"MSHTML 11.00.9600.17126\">\r\n<STYLE></STYLE>\r\n\r\n<STYLE></STYLE>\r\n\r\n<STYLE></STYLE>\r\n\r\n<STYLE></STYLE>\r\n\r\n<STYLE></STYLE>\r\n\r\n<STYLE></STYLE>\r\n\r\n<STYLE></STYLE>\r\n\r\n<STYLE></STYLE>\r\n\r\n<STYLE></STYLE>\r\n\r\n\r\n<!--Announced by Visual SiteBuilder 9-->\r\n<link rel=\"stylesheet\" type=\"text/css\" href=\"_sitegray/_sitegray_d.css\" />\r\n<script language=\"javascript\" src=\"_sitegray/_sitegray.js\"></script>\r\n<!-- CustomerNO:7765626265723230797347545255544303060000 -->\r\n<link rel=\"stylesheet\" type=\"text/css\" href=\"index.vsb.css\" />\r\n<META Name=\"keywords\" Content=\"盘锦校区主页\" />\r\n<script type=\"text/javascript\" src=\"/system/resource/js/counter.js\"></script>\r\n<script type=\"text/javascript\">_jsq_(1001,'/index.jsp',-1,1085683818)</script>\r\n</HEAD>\r\n<BODY><!--对联广告开始--><script language=\"JavaScript\">\r\nvar delta=0.2;\r\nvar collection;\r\nvar closeB=false;\r\nfunction floaters() {\r\nthis.items = [];\r\nthis.addItem = function(id,x,y,content)\r\n{\r\ndocument.write('<DIV id='+id+' align=\"center\" style=\"Z-INDEX: 10; POSITION: absolute; width:100px; he