r\n            if(!u_u9_move)\r\n                return;\r\n            \r\n            var demoobj = document.getElementById(\"u_u9_demo\");\r\n            var demo1obj = document.getElementById(\"u_u9_demo1\");\r\n\r\n            //滚动步长\r\n            var scrollstep = 1\r\n            \r\n            //当前位置\r\n            var oldscrollLeft = demoobj.scrollLeft;\r\n            \r\n            //更新后的位置, 已考虑过回滚\r\n            var newscrollLeft = (oldscrollLeft + scrollstep)%demo1obj.scrollWidth;\r\n\r\n            \r\n            //间歇滚动            \r\n            if(u_u9_pos == null)\r\n                u_u9_initpos();\r\n            //滚动前td\r\n            var oldpos = u_u9_getpos(oldscrollLeft);\r\n            //滚动后td\r\n            var newpos = u_u9_getpos(newscrollLeft);\r\n            //经过td数量\r\n            if(oldpos != newpos)\r\n            {\r\n                u_u9_movenum++;\r\n            }\r\n            //达到数量， 停留一定时间\r\n\r\n            if(u_u9_movenum >= 1)\r\n            {\r\n                u_u9_movenum = 0;\r\n                newscrollLeft = u_u9_pos[newpos];\r\n                u_u9_clearInterval();\r\n                setTimeout(u_u9_setInterval, 6*1000);\r\n            }\r\n            \r\n            demoobj.scrollLeft = newscrollLeft; \r\n            \r\n        } \r\n        catch(e) \r\n        { \r\n        } \r\n    } \r\n\r\n    function u_u9_clearInterval()\r\n    {\r\n        clearInterval(u_u9_MyMar);\r\n    }\r\n    \r\n    function u_u9_setInterval()\r\n    {\r\n        u_u9_MyMar = setInterval(u_u9_Marquee,u_u9_speed3);\r\n    }\r\n    \r\n    function u_u9_stopmove()\r\n    {\r\n        u_u9_move = false;\r\n    }\r\n\r\n    function u_u9_startmove()\r\n    {\r\n        u_u9_move = true;\r\n    }\r\n    \r\n    function u_u9_init()\r\n    {\r\n        u_u9_speed3 = 5;\r\n        if(u_u9_speed3 < 1)\r\n        {\r\n            u_u9_speed3 = 1;\r\n        }\r\n        \r\n        document.getElementById(\"u_u9_demo\").onmouseover = u_u9_stopmove;\r\n        document.getElementById(\"u_u9_demo\").onmouseout = u_u9_startmove;\r\n\r\n        document.getElementById(\"u_u9_demo2\").innerHTML = document.getElementById(\"u_u9_demo1\").innerHTML;\r\n        \r\n        u_u9_init_img();\r\n        \r\n        setTimeout(u_u9_setInterval, 6*1000);\r\n\r\n    }\r\n    \r\n    u_u9_init();\r\n</SCRIPT><!--#endeditable--></div></div>\r\n<div id=\"footer\">\r\n<div class=\"foot\"><!--div class=\"foot_l\"-->版权所有：大连理工大学&nbsp;&nbsp;地址：中国·辽宁省大连市甘井子区凌工路2号&nbsp;&nbsp;邮编：116024<br>辽ICP备05001357号&nbsp;&nbsp;电话：0411-84708320 总点击量：<!--#begineditable name=\"文字计数器-总点击量\" viewid=\"130096\" tagname=\"文字计数器-总点击量\" action=\"\" layout=\"\" contype=\"\" stylesysid=\"\" template=\"\" tplstyle=\"\" clone=\"\" istemp=\"\"--><script language=\"javascript\" src=\"_dwr/engine.js\"></script><script language=\"javascript\" src=\"_dwr/util.js\"></script><script language=\"javascript\" src=\"_dwr/interface/PageCounterDWR.js\"></script><span id=\"a130096\" name=\"a130096\"></span>   \r\n\r\n<script>\r\nfunction getvisittimea130096()\r\n{       \r\n    PageCounterDWR.getVisittime('1027873887','1002','TOTAL_COUNTER','0','8',callbackdataa130096)\r\n}\r\nfunction callbackdataa130096(data)\r\n{\r\n    document.getElementById(\"a130096\").innerHTML = data; \r\n}\r\ngetvisittimea130096();\r\n</script><!--#endeditable--> 今日点击量：<!--#begineditable name=\"文字计数器-今日\" viewid=\"130097\" tagname=\"文字计数器-今日\" action=\"\" layout=\"\" contype=\"\" stylesysid=\"\" template=\"\" tplstyle=\"\" clone=\"\" istemp=\"\"--><span id=\"a130097\" name=\"a130097\"></span>   \r\n\r\n<script>\r\nfunction getvisittimea130097()\r\n{       \r\n    PageCounterDWR.getVisittime('1027873887','1002','DATE_COUNTER','0','5',callbackdataa130097)\r\n}\r\nfunction callbackdataa130097(data)\r\n{\r\n    document.getElementById(\"a130097\").innerHTML = data; \r\n}\r\ngetvisittimea130097();\r\n</script><!--#endeditable--><!--/div--> <!--div class=\"foot_r\"><a href=\"#\">网站地图</a> | <a href